#!/bin/bash
#set -vx
#=========================================================================================
# 作成日　　　：2017/9/24
# 作成者　　　：安藤 達
# 引数　　　　：なし
# 概要　　　　；dfコマンドを利用し、戻り値にディスク使用率を出力する
#=========================================================================================

#=========================================================================================
# 変数
#=========================================================================================

#df -h --total実行時のトータル行がある列を代入
total_line=""

#=========================================================================================
# 本処理
#=========================================================================================

# 変数total_lineにdf -h --totlaのトータルが記載されている行のみ代入
total_line="$(df -h --total | grep total)"

# 変数total_lineのディスク使用率の部分のみ抽出
echo ${total_line} | awk '{print $5}' | sed -e 's/%//g'

exit 0
